import React , { Component } from 'react'
import {Link} from 'react-router-dom'
import loadGif from '../loadgif.gif'

class Home extends Component {
  state = {
    posts: [],
  };

  componentDidMount() {

    fetch("https://www.breakingbadapi.com/api/characters")
      .then((res) => {
        return res.json();
      })
      .then((res) => {
        // console.log(res.slice(0,20));
        this.setState({
          posts: res.slice(0, 25),
        });
      });

  }

  render() {
    const data = this.state.posts.length ? (
      this.state.posts.map((post) => {
        return (
          
            <div className="pots" key={post.char_id}>
            <img
              src={post.img}
              alt="boohoo"
              className="img-responsive"
              id="fetch-img"
            />
            <h6>{post.name.substring(0, 16)}</h6>
            <Link to={"/" + post.char_id}><button className="button button2">Read more</button></Link>
          </div>
        );
      })
    ) : (
      <div>
          <img src={loadGif} alt="Loading" id="loading"/>
      </div>
    );
    return <div>{data}</div>;
  }
}

export default Home
