import React, { Component } from "react";
import loadGif from '../loadgif.gif'

class Show extends Component {
    state = {
        posts : []
    }
    
    componentDidMount(){
        console.log(this.props);
        
        const id = this.props.match.params.id;
        fetch("https://www.breakingbadapi.com/api/characters/" + id)
          .then((res) => {
            return res.json();
          })
          .then((res) => {
              console.log(res);
            this.setState({
                posts : res
            })
          });    
    }

    render() {
        const data = this.state.posts.length ? (
          this.state.posts.map((post) => {
            return (
              <div className="showPost" key={post.char_id}>
                <img src={post.img} alt="boohoo" className="img-responsive" id="fetch-img-show" /><br /><br />
                <div>
                  <h5 className="showpostName">Name : {post.name}</h5>
                  <p>Nickname : {post.nickname}</p>
                  <p>Birth : {post.birthday}</p>
                  <p>Occupation : {post.occupation[0]}</p>
                  <p>{post.nickname} is simply dummy text of the printing 
                  and typesetting industry. Lorem Ipsum has been the s 
                  standard dummy text ever since the 1500s, when an un
                   printer took a galley of type and scrambled it to m 
                   type specimen book. It has survived not only five c
                   enturies, but also the leap into electronic typesett
                  remaining essentially unchanged. It was posts of 1960s</p>
                </div>

              </div>
            );
          })
        ) : (
          <div>
              <img src={loadGif} alt="Loading" id="loading" />
          </div>
        );
        return <div>{data}</div>;
    }
}

export default Show;
